sys_variable_value.value:
(function execute(inputs, outputs) {

  var inputScope = inputs.flow_scope_sys_id;
  var triggerTable = inputs.trigger_table;
  var newFlow = inputs.new_flow_sys_id;
  var connectionCredentialAlias = inputs.connection_credential_alias;

  // Update Trigger Table
  sn_flow.FlowTemplates.updateFlowTrigger(newFlow, 'table', triggerTable);

  // update connection_credential_alias
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'connection_credential_alias', connectionCredentialAlias);

})(inputs, outputs);

