sys_variable_value.value:
( function execute(inputs, outputs) {


  var inputScope = inputs.flow_scope_sys_id;
  var newFlow = inputs.new_flow_sys_id;

  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'customer_type', inputs.customerType);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'case_type', inputs.caseType);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'is_escalated', inputs.isEsacalated);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'priority', inputs.priority);
  new global.FlowTemplatesHelper().activateConnection(inputs.connection);

})(inputs, outputs);

