sys_variable_value.value:
(function execute(inputs, outputs) {
	var inputScope = inputs.flow_scope_sys_id;
    var newFlow = inputs.new_flow_sys_id;
    var catalogItem = inputs.catalog_item;
    var connection = inputs.connection;
    ;
    //Update the catalog item 
    sn_flow.FlowTemplates.updateFlowActions(newFlow,'1', 'template_catalog_item', catalogItem);

    new global.FlowTemplatesHelper().activateConnection(connection);
})(inputs, outputs);

